import * as React from "react";
import "./styles/style.css";
import Content from "./components/Content";
import { getDefinitions } from "./api/contentAPI";
import { useEffect, useState } from "react";
import { Layout } from "./types/model";

export default function App() {
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState<Layout | null>(null);
  // load definition here
  useEffect(() => {
    getDefinitions()
      .then((response) => setData(response.data))
      .catch((error) => {
        console.log("Error fetching data: ", error);
        setError(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) return "Loading...";
  if (error) return "Error!";

  return (
    <div className="main">
      {/* TODO remove title usage from template */}
      <h1>{data?.title}</h1>
      <div className="content">{data && <Content data={data} />}</div>
    </div>
  );
}
